import {createApi, fetchBaseQuery} from '@reduxjs/toolkit/query/react';

export const spaceXApi = createApi({
    reducerPath: 'spacexApi',
    baseQuery: fetchBaseQuery({ baseUrl: 'https://api.spacexdata.com/v5' }),
    tagTypes: [],
    endpoints: (builder) => ({
        getLaunches: builder.query({
            query: (args) => ({
                url: '/launches/query',
                method: 'POST',
                body: {
                    "query":{
                        "success":true,
                        "date_utc": {
                            "$gte": "2015-01-01T00:00:00.000Z",
                            "$lte": "2019-12-31T00:00:00.000Z"
                        }
                    },
                    "options":{
                        "sort":{
                            "data_utc": "asc"
                        }
                    }
                }
            }),
        }),
    }),
})

export const {useGetLaunchesQuery} = spaceXApi;
