import {createApi, fetchBaseQuery} from '@reduxjs/toolkit/query/react';

export const spaceXApi = createApi({
    reducerPath: 'spacexApi',
    baseQuery: fetchBaseQuery({ baseUrl: 'https://api.spacexdata.com/v5' }),
    tagTypes: [],
    endpoints: (builder) => ({
        launches: builder.query({
            query: () => '/launches/query'
        }),
        getLaunches: builder.mutation({
            query: (args) => ({
                url: '/launches/query',
                method: 'POST',
                body: JSON.stringify({
                    query: {
                        success: true
                    },
                    options: {
                        sort: {
                            data_utc: args
                        }
                    }
                })
            }),
        }),
    }),
})

export const {useGetLaunchesQuery, useLaunchesQuery} = spaceXApi;
