import {createApi, fetchBaseQuery} from '@reduxjs/toolkit/query/react';

export const spaceXApi = createApi({
    reducerPath: 'spacexApi',
    baseQuery: fetchBaseQuery({baseUrl: 'https://api.spacexdata.com/v5/launches/query'}),
    tagTypes: [],
    endpoints: (builder) => ({
        getLaunches: builder.query({
                "date_utc": {
                    "$gte": "2017-06-22T00:00:00.000Z",
                    "$lte": "2017-06-25T00:00:00.000Z"
                },
                "$or": [
                    {
                        "flight_number": {
                            "$gt": 30
                        }
                    },
                    {
                        "tbd": true
                    }
                ],
                "date_precision": {
                    "$in": [
                        "month",
                        "day"
                    ]
                }
            ,
            "options": {
                "sort": {
                    "flight_number": "asc"
                },
                "limit": 50
            }
        }),
    }),
})

export const {useGetLaunchesQuery} = spaceXApi;
